public function index()
{
    $code = $_GET["code"];
    if (isset($_GET['code'])){
        $userinfo = $this->get_user_info($code);
        $wechat_id = model('member_wechat')->where(['unionid'=>$userinfo['unionid']])->value('id')
        if($wechat_id){
            model('member_wechat')->where(['id'=>$wechat_id])->update($userinfo);//将数据写入到数据库
