                'reg_time'    =>    time(),
                'login_time'  =>    time(),
                'login_ip'    =>    get_client_ip(),
                'status'      =>    1,
            );
            $member_id = model('member')->insertGetId($data);
        }else{
            $data = array(
                'nickname'    =>    $userinfo['nickname'],//呢称
                'photo'       =>    $userinfo['headimgurl'],//头像
                'login_time'  =>    time(),
                'login_ip'    =>    get_client_ip(),
            );
            model('member')->where(['id'=>$member_id])->insertGetId($data);
        }
