function saveImage1($path) {
    if(!preg_match('/\/([^\/]+\.[a-z]{3,4})$/i',$path,$matches)){
        die('Use image please');
    }
    $image_name = strToLower($matches[1]);
    $ch = curl_init ($path);
    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);
